import React, { useState } from "react";
import Table from "./Components/table";
import "./App.css";
// import GetDataExport from "./dataFetch";

function App() {
  const [propertyData, setPropertyData] = React.useState(null);

  const [textInput, setMessage] = useState("");
  const [searchOption, setSearchOption] = useState("lrPropertyOutcode");

  const handleOptionChange = (event) => {
    setSearchOption(event.target.value);
  };
  const handleSearchtermChange = (event) => {
    setMessage(event.target.value);
  };
  const handleKeyDown = (event) => {
    if (event.key === "Enter")
      fetchData(textInput.toUpperCase())
  };

  // const search = ()=> {
  //   const search = message.toUpperCase();
  //   featchData(search);
  //   // const searchString = `/${searchOption}/${search}`
  //   // const data = GetDataExport(searchString);
  //   // console.log("Exported data: ",data)
  // }


  const fetchData = async (search) => {
    const resp = await fetch(`/${searchOption}/${search}`);
    const json = await resp.json();

    if (json.success) {
      let tempArr = []
      if(searchOption === "lrPropertyId"){
        tempArr.push(json.lrProperty)
        setPropertyData(tempArr)
      } else {
        tempArr = json.lrProperty;
        setPropertyData(tempArr);
      }
    }
  };

  return (
    <div className="App" data-testid="app">
        <div data-testid="input-container" className="inputContainer">
          <input
            className="textInput"
            data-testid="input-search"
            type="text"
            placeholder="Press enter to search"
            value={textInput}
            onChange={handleSearchtermChange}
            onKeyDown={handleKeyDown}
          />
          <select className="optionSelect" value={searchOption} onChange={handleOptionChange}>
            <option value="lrPropertyOutcode">Outcode</option>
            <option value="lrPropertyStreet">Street</option>
            <option value="lrPropertyId">ID</option>
          </select>
        </div>
        {propertyData ? 
          <Table data-testid="app-table" propertyData={propertyData} />
         :
          <div data-testid="app-welcome-message" className="app__welcomeMessage">
            <p>
              Please enter an Id, outcode, or Street to find related properties
              <br></br>
              Then select the property to see related transactions
            </p>
          </div>
        }
    </div>
  );
}

export default App;
